import React from 'react';

import Aux from '../../../hoc/Auxilary/Auxillary';
import Button from '../../UI/Button/Button';

const OrderSummary = (props) => {
    const ingredientSummary = Object.keys(props.ingredients)
        .map( igKey => {
            return (
                <li key={igKey}>
                    <span style={{ textTransform: 'capitalize' }}>{igKey}</span>: {props.ingredients[igKey]}
                </li> );
        } );

    return (
        <Aux>
            <h3>Your Order</h3>
            <p>A delicious burger with the following ingredients:</p>
            <ul>
                {ingredientSummary}
            </ul>
            <p><strong>Burger Price: $ {props.price.toFixed(2)}</strong></p>
            <p><strong>Vat:$ {props.vat.toFixed(2)}</strong></p>
            <p><strong>Total Price: {props.vatPrice.toFixed(2)}</strong></p>
            <p>For continue, Please click Checkout.</p>
            <Button btnType="Danger" clicked={props.purchaseCancelled}>CANCEL</Button>
            <Button btnType="Success" clicked={props.purchaseContinued}>CHECKOUT</Button>
        </Aux>
    );
}

export default OrderSummary;
