import React, { Component } from 'react';

import Button from '../../components/UI/Button/Button';
import classes from './UserInfo.module.css';
import Input from '../../components/UI/Input/Input';

class UserInfo extends Component {
    state = {
        info: {
            name: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Your Name'
                },
                value: '',
                validation: {
                    required: true
                },
                valid: false,
                touched: false
            },
            number: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Your Phone Number'
                },
                value: '',
                validation: {
                    required: true
                },
                valid: false,
                touched: false
            },
        }
    }
    continueHandler = () => {
        this.props.history.push('/');
    }

    render () {
        return (
            <div className={classes.UserInfo}>
                <h4>Please enter your information</h4>
                <form>
                    <Input elementType="input" type="text" name="name" placeholder="Your Name" />
                    <Input elementType="input" type="text" name="number" placeholder="Your Phone Number" />
                    <Button btnType="Success" clicked={this.continueHandler}>CONTINUE</Button>
                </form>
            </div>
        );
    }
}

export default UserInfo;
